// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./TurnVerifier.sol";

// =========================================================================
// Pyth Entropy V2 Interface (Monad Testnet)
// =========================================================================

/**
 * @title IEntropyV2
 * @notice Interface for Pyth Entropy V2 on Monad
 */
interface IEntropyV2 {
    /**
     * @notice Request randomness (simplest form - uses default provider)
     * @return sequenceNumber Unique identifier for this request
     */
    function requestV2() external payable returns (uint64 sequenceNumber);
    
    /**
     * @notice Get the fee required for a randomness request
     * @return fee The fee in wei
     */
    function getFeeV2() external view returns (uint128 fee);
}

/**
 * @title IEntropyConsumer
 * @notice Interface that contracts must implement to receive entropy callbacks
 */
interface IEntropyConsumer {
    /**
     * @notice Called by Entropy contract with the random number
     * @param sequenceNumber The sequence number from the request
     * @param provider The provider that generated the randomness
     * @param randomNumber The random number generated
     */
    function _entropyCallback(
        uint64 sequenceNumber,
        address provider,
        bytes32 randomNumber
    ) external;
}

/**
 * @title FateboundCore
 * @author Fatebound Breach Team
 * @notice Main game contract with Pyth Entropy V2 VRF integration
 * @dev Uses Pyth Entropy V2 for verifiable randomness on Monad Testnet
 */
contract FateboundCore is IEntropyConsumer {
    using TurnVerifier for *;

    // =========================================================================
    // Constants
    // =========================================================================
    
    /// @notice Pyth Entropy V2 contract address on Monad Testnet (Verified working address)
    address public constant ENTROPY = 0x825c0390f379C631f3Cf11A82a37D20BddF93c07;
    
    /// @notice Default player HP for Level 1
    uint16 constant DEFAULT_PLAYER_HP = 100;

    // =========================================================================
    // Events
    // =========================================================================
    
    event GameRequested(
        uint256 indexed gameId,
        address indexed player,
        uint64 sequenceNumber
    );
    
    event GameStarted(
        uint256 indexed gameId,
        address indexed player,
        bytes32 seed
    );
    
    event TurnSubmitted(
        uint256 indexed gameId,
        uint8 turn,
        bytes32 stateHash,
        TurnVerifier.TurnResult result
    );
    
    event GameCompleted(
        uint256 indexed gameId,
        address indexed player,
        uint256 score // Updated to uint256 as requested
    );

    // =========================================================================
    // Enums
    // =========================================================================
    
    enum GameStatus {
        NONE,
        WAITING_FOR_VRF,
        ACTIVE,
        COMPLETED
    }

    // =========================================================================
    // Structs
    // =========================================================================
    
    /// @notice Game session data
    struct Game {
        address player;
        bytes32 seed;
        bytes32 stateHash;
        uint8 currentTurn;
        uint32 score; // Track score on-chain
        GameStatus status;
        TurnVerifier.TurnResult result;
    }

    // =========================================================================
    // State Variables
    // =========================================================================
    
    /// @notice Game counter for unique IDs
    uint256 public gameCounter;
    
    /// @notice All games by ID
    mapping(uint256 => Game) public games;
    
    /// @notice Active game per player (one game at a time)
    mapping(address => uint256) public activeGameId;
    
    /// @notice Maps Pyth sequence numbers to game IDs for callback routing
    mapping(uint64 => uint256) public sequenceToGameId;

    // =========================================================================
    // Modifiers
    // =========================================================================
    
    /// @notice Ensures only the Entropy contract can call the callback
    modifier onlyEntropy() {
        require(msg.sender == ENTROPY, "Only Entropy contract can call");
        _;
    }

    // =========================================================================
    // Constructor
    // =========================================================================
    
    constructor() {
        // No initialization needed - uses constant ENTROPY address
    }

    // =========================================================================
    // Game Management - VRF Flow
    // =========================================================================
    
    /**
     * @notice Starts a new game by requesting VRF randomness
     * @dev Payable - requires sending enough ETH to cover VRF fee
     * @return gameId The new game's ID
     * @return sequenceNumber The Pyth sequence number for tracking
     */
    function startGame() external payable returns (
        uint256 gameId,
        uint64 sequenceNumber
    ) {
        // Check player doesn't have an active game in progress
        uint256 existingGame = activeGameId[msg.sender];
        if (existingGame != 0) {
            GameStatus existingStatus = games[existingGame].status;
            require(
                existingStatus == GameStatus.COMPLETED || existingStatus == GameStatus.NONE,
                "Complete or forfeit existing game first"
            );
        }
        
        // Calculate and verify fee using V2 interface
        uint128 fee = IEntropyV2(ENTROPY).getFeeV2();
        require(msg.value >= fee, "Insufficient fee for VRF");
        
        // Create game record
        gameCounter++;
        gameId = gameCounter;
        
        games[gameId] = Game({
            player: msg.sender,
            seed: bytes32(0), // Will be set in callback
            stateHash: bytes32(0),
            currentTurn: 0,
            score: 0,
            status: GameStatus.WAITING_FOR_VRF,
            result: TurnVerifier.TurnResult.CONTINUE
        });
        
        activeGameId[msg.sender] = gameId;
        
        // Request randomness from Pyth Entropy V2 (simplest form)
        sequenceNumber = IEntropyV2(ENTROPY).requestV2{value: fee}();
        
        // Map sequence to game for callback
        sequenceToGameId[sequenceNumber] = gameId;
        
        emit GameRequested(gameId, msg.sender, sequenceNumber);
        
        // Refund excess ETH
        if (msg.value > fee) {
            payable(msg.sender).transfer(msg.value - fee);
        }
    }
    
    /**
     * @notice Callback from Pyth Entropy with the random number
     * @dev Only callable by the Entropy contract (V2 callback signature)
     * @param sequenceNumber The sequence number from the request
     * @param _provider The provider address (unused but required by interface)
     * @param randomNumber The VRF-generated random number
     */
    function _entropyCallback(
        uint64 sequenceNumber,
        address _provider,
        bytes32 randomNumber
    ) external override onlyEntropy {
        _provider; // Silence unused variable warning
        
        uint256 gameId = sequenceToGameId[sequenceNumber];
        require(gameId != 0, "Unknown sequence number");
        
        Game storage game = games[gameId];
        require(game.status == GameStatus.WAITING_FOR_VRF, "Game not waiting for VRF");
        
        // Set the seed and initialize game state
        game.seed = randomNumber;
        game.currentTurn = 1;
        game.status = GameStatus.ACTIVE;
        
        // Compute initial state hash
        TurnVerifier.Player memory initialPlayer = TurnVerifier.Player({
            hp: DEFAULT_PLAYER_HP,
            maxHp: DEFAULT_PLAYER_HP,
            shield: 0
        });
        
        TurnVerifier.Enemy[] memory initialEnemies = _getDefaultEnemies();
        
        game.stateHash = TurnVerifier.computeStateHash(
            initialPlayer,
            initialEnemies,
            1,
            0 // Initial score
        );
        
        // Clean up mapping
        delete sequenceToGameId[sequenceNumber];
        
        emit GameStarted(gameId, game.player, randomNumber);
    }
    
    /**
     * @notice Submits a turn for verification
     * @param gameId The game ID
     * @param player Claimed player state before turn
     * @param enemies Claimed enemy states before turn
     * @param assignments Packet-to-target assignments
     */
    function submitTurn(
        uint256 gameId,
        TurnVerifier.Player memory player,
        TurnVerifier.Enemy[] memory enemies,
        TurnVerifier.Assignment[] memory assignments
    ) external {
        Game storage game = games[gameId];
        
        require(msg.sender == game.player, "Not your game");
        require(game.status == GameStatus.ACTIVE, "Game not active");
        
        // Verify claimed pre-state matches stored hash
        // (Note: computeStateHash signature changed)
        bytes32 claimedStateHash = TurnVerifier.computeStateHash(
            player,
            enemies,
            game.currentTurn,
            game.score
        );
        require(claimedStateHash == game.stateHash, "State mismatch - invalid pre-state");
        
        // Resolve turn using deterministic logic
        (
            TurnVerifier.Player memory newPlayer,
            TurnVerifier.Enemy[] memory newEnemies,
            TurnVerifier.TurnResult result,
            uint32 newScore
        ) = TurnVerifier.resolveTurn(
            game.seed,
            player,
            enemies,
            game.currentTurn,
            assignments,
            game.score
        );
        
        // Update game state
        game.currentTurn++;
        game.score = newScore;
        game.stateHash = TurnVerifier.computeStateHash(
            newPlayer,
            newEnemies,
            game.currentTurn,
            newScore
        );
        
        emit TurnSubmitted(gameId, game.currentTurn - 1, game.stateHash, result);
        
        // Handle game end
        if (result != TurnVerifier.TurnResult.CONTINUE) {
            game.status = GameStatus.COMPLETED;
            game.result = result;
            
            emit GameCompleted(
                gameId, 
                msg.sender, 
                newScore
            );
        }
    }

    /**
     * @notice Claims victory and submits verification data.
     * @dev Alternative to submitTurn if we move fully client-side verification.
     *      For now, we trust submitTurn.
     *      BUT current task asks for a specific claimVictory.
     */
    /**
     * @notice Claims victory and submits verification data.
     * @param gameId The ID of the game to claim.
     * @param claimedScore The final score claimed by the player.
     * @dev We are using "Optimistic Scoring" for this prototype.
     */
    function claimVictory(
        uint256 gameId, 
        uint256 claimedScore
    ) external {
        Game storage game = games[gameId];
        require(msg.sender == game.player, "Not your game");
        require(game.status == GameStatus.ACTIVE, "Game not active");
        
        // Optimistic: We trust the score in this specific claim function
        game.status = GameStatus.COMPLETED;
        game.result = TurnVerifier.TurnResult.VICTORY;
        // Cast to uint32 for storage, assuming it fits.
        game.score = uint32(claimedScore); 
        
        emit GameCompleted(
            gameId, 
            msg.sender, 
            claimedScore
        );
    }
    
    /**
     * @notice Forfeits an active game
     */
    function forfeitGame(uint256 gameId) external {
        Game storage game = games[gameId];
        require(msg.sender == game.player, "Not your game");
        require(
            game.status == GameStatus.ACTIVE || game.status == GameStatus.WAITING_FOR_VRF,
            "Game not active"
        );
        
        game.status = GameStatus.COMPLETED;
        game.result = TurnVerifier.TurnResult.DEFEAT;
        
        emit GameCompleted(
            gameId, 
            msg.sender, 
            game.score
        );
    }

    // =========================================================================
    // View Functions
    // =========================================================================
    
    /**
     * @notice Gets the current VRF fee required to start a game
     * @return fee The fee in wei
     */
    function getVRFFee() external view returns (uint128) {
        return IEntropyV2(ENTROPY).getFeeV2();
    }
    
    /**
     * @notice Gets full game information
     */
    function getGame(uint256 gameId) external view returns (
        address player,
        bytes32 seed,
        bytes32 stateHash,
        uint8 currentTurn,
        GameStatus status,
        TurnVerifier.TurnResult result
    ) {
        Game storage game = games[gameId];
        return (
            game.player,
            game.seed,
            game.stateHash,
            game.currentTurn,
            game.status,
            game.result
        );
    }
    
    /**
     * @notice Gets the current game state hash for a game
     */
    function getGameStateHash(uint256 gameId) external view returns (bytes32) {
        return games[gameId].stateHash;
    }
    
    /**
     * @notice Checks if a player has an active game
     */
    function hasActiveGame(address player) external view returns (bool) {
        uint256 gameId = activeGameId[player];
        if (gameId == 0) return false;
        GameStatus status = games[gameId].status;
        return status == GameStatus.ACTIVE || status == GameStatus.WAITING_FOR_VRF;
    }
    
    /**
     * @notice Gets the hand of packets for a given turn
     */
    function getHand(
        bytes32 seed, 
        uint8 turn
    ) external pure returns (TurnVerifier.Packet[] memory) {
        return TurnVerifier.deriveHand(seed, turn);
    }
    
    /**
     * @notice Simulates a turn resolution
     */
    function simulateTurn(
        bytes32 seed,
        TurnVerifier.Player memory player,
        TurnVerifier.Enemy[] memory enemies,
        uint8 turn,
        TurnVerifier.Assignment[] memory assignments,
        uint32 initialScore
    ) external pure returns (
        TurnVerifier.Player memory newPlayer,
        TurnVerifier.Enemy[] memory newEnemies,
        TurnVerifier.TurnResult result,
        uint32 newScore
    ) {
        return TurnVerifier.resolveTurn(seed, player, enemies, turn, assignments, initialScore);
    }

    // =========================================================================
    // Internal Functions
    // =========================================================================
    
    /**
     * @notice Returns the default enemy configuration (Level 1)
     */
    function _getDefaultEnemies() internal pure returns (TurnVerifier.Enemy[] memory) {
        TurnVerifier.Enemy[] memory enemies = new TurnVerifier.Enemy[](3);
        
        // FIREWALL_A
        enemies[0] = TurnVerifier.Enemy({
            id: 0,
            hp: 20,
            maxHp: 20,
            damage: 5,
            isAttacking: true,
            isCache: false
        });
        
        // DRONE_B
        enemies[1] = TurnVerifier.Enemy({
            id: 1,
            hp: 15,
            maxHp: 15,
            damage: 3,
            isAttacking: true,
            isCache: false
        });
        
        // SENTINEL_C
        enemies[2] = TurnVerifier.Enemy({
            id: 2,
            hp: 30,
            maxHp: 30,
            damage: 8,
            isAttacking: false,
            isCache: false
        });
        
        return enemies;
    }

    // =========================================================================
    // Receive ETH
    // =========================================================================
    
    receive() external payable {}
}

contracts/TurnVerifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title TurnVerifier
 * @author Fatebound Breach Team
 * @notice Deterministic game logic library that mirrors vrfMapper.ts and turnResolver.ts
 * @dev This contract MUST produce the exact same results as the TypeScript version.
 *      Used for on-chain verification of game outcomes.
 * 
 * CRITICAL: All logic here must be pure/deterministic. Same inputs = same outputs.
 */
library TurnVerifier {
    // =========================================================================
    // Constants - Must match gameState.ts PACKET_THRESHOLDS and PACKET_VALUES
    // =========================================================================
    
    // Packet Type Enum (matches TypeScript PacketType)
    enum PacketType { MISS, ATTACK, DEFEND, CRIT, HEAL }
    
    // Byte-to-Packet Thresholds (from vrfMapper.ts)
    // | Range     | Type   | Effect      |
    // |-----------|--------|-------------|
    // | 0-19      | MISS   | 0 Damage    |
    // | 20-150    | ATTACK | 5 Damage    |
    // | 151-200   | DEFEND | 5 Shield    |
    // | 201-240   | CRIT   | 15 Damage   |
    // | 241-255   | HEAL   | 10 HP       |
    uint8 constant THRESHOLD_MISS   = 19;
    uint8 constant THRESHOLD_ATTACK = 150;
    uint8 constant THRESHOLD_DEFEND = 200;
    uint8 constant THRESHOLD_CRIT   = 240;
    // 241-255 = HEAL
    
    // Packet effect values (from PACKET_VALUES in gameState.ts)
    uint8 constant VALUE_MISS   = 0;
    uint8 constant VALUE_ATTACK = 5;
    uint8 constant VALUE_DEFEND = 5;
    uint8 constant VALUE_CRIT   = 15;
    uint8 constant VALUE_HEAL   = 10;
    
    // =========================================================================
    // Structs - Mirror TypeScript interfaces
    // =========================================================================
    
    /// @notice A single packet derived from VRF bytes
    struct Packet {
        uint8 byteValue;        // Raw byte (0-255)
        PacketType packetType;  // Derived type
        uint8 value;            // Effect value
    }
    
    /// @notice Player state
    struct Player {
        uint16 hp;
        uint16 maxHp;
        uint16 shield;
    }
    
    /// @notice Enemy state
    // Flag for CACHE_GOLD (using high ID or specific property is better, 
    // but for simplicity we'll assume ID 99 or specific bool isCache)
    // Actually, let's add isCache to Enemy struct.
    struct Enemy {
        uint8 id;
        uint16 hp;
        uint16 maxHp;
        uint8 damage;       // Intent damage
        bool isAttacking;   // true if intent is ATTACK
        bool isCache;       // true if it's a loot cache (Zero-Day Cache)
    }
    
    /// @notice Complete game state for verification
    struct GameState {
        Player player;
        Enemy[] enemies;
        uint8 turnCounter;
        uint32 score; // Added score
    }

    /// @notice Assignment - maps packet index to target
    /// @dev targetType: 0 = trash, 1 = player, 2+ = enemy index + 2
    struct Assignment {
        uint8 packetIndex;
        uint8 targetType;   // 0=trash, 1=player, 2=enemy0, 3=enemy1, etc.
    }
    
    /// @notice Turn result after resolution
    enum TurnResult { CONTINUE, VICTORY, DEFEAT }
    
    // =========================================================================
    // VRF Mapper Functions (mirrors vrfMapper.ts)
    // =========================================================================
    
    /**
     * @notice Maps a single byte (0-255) to a PacketType
     * @dev Exact mirror of mapByteToPacketType() in vrfMapper.ts
     * @param byteValue The raw VRF byte
     * @return The derived PacketType
     */
    function mapByteToPacketType(uint8 byteValue) internal pure returns (PacketType) {
        if (byteValue <= THRESHOLD_MISS) {
            return PacketType.MISS;
        }
        if (byteValue <= THRESHOLD_ATTACK) {
            return PacketType.ATTACK;
        }
        if (byteValue <= THRESHOLD_DEFEND) {
            return PacketType.DEFEND;
        }
        if (byteValue <= THRESHOLD_CRIT) {
            return PacketType.CRIT;
        }
        return PacketType.HEAL;
    }
    
    /**
     * @notice Gets the effect value for a packet type
     * @dev Mirror of PACKET_VALUES lookup in vrfMapper.ts
     */
    function getPacketValue(PacketType pType) internal pure returns (uint8) {
        if (pType == PacketType.MISS) return VALUE_MISS;
        if (pType == PacketType.ATTACK) return VALUE_ATTACK;
        if (pType == PacketType.DEFEND) return VALUE_DEFEND;
        if (pType == PacketType.CRIT) return VALUE_CRIT;
        if (pType == PacketType.HEAL) return VALUE_HEAL;
        return 0;
    }
    
    /**
     * @notice Calculates number of packets for a given turn
     * @dev Formula: 5 + (turn % 3) - matches calculatePacketCount() in vrfMapper.ts
     * @param turn Turn number (1-indexed)
     * @return Number of packets for this turn
     */
    function calculatePacketCount(uint8 turn) internal pure returns (uint8) {
        return 5 + (turn % 3);
    }
    
    /**
     * @notice Derives the turn-specific seed from level seed
     * @dev Uses keccak256(levelSeed + "TurnN") - matches deriveTurnSeed() in vrfMapper.ts
     */
    function deriveTurnSeed(bytes32 levelSeed, uint8 turn) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked(levelSeed, "Turn", turn));
    }
    
    /**
     * @notice Derives a hand of packets from level seed and turn number
     * @dev Main entry point - mirrors derivePacketsFromSeed() in vrfMapper.ts
     * @param levelSeed The VRF level seed
     * @param turn Current turn number
     * @return packets Array of derived Packet structs
     */
    function deriveHand(
        bytes32 levelSeed, 
        uint8 turn
    ) internal pure returns (Packet[] memory packets) {
        uint8 packetCount = calculatePacketCount(turn);
        bytes32 turnSeed = deriveTurnSeed(levelSeed, turn);
        
        packets = new Packet[](packetCount);
        
        for (uint8 i = 0; i < packetCount; i++) {
            // Each byte of the hash maps to one packet
            uint8 byteValue = uint8(turnSeed[i % 32]);
            PacketType pType = mapByteToPacketType(byteValue);
            
            packets[i] = Packet({
                byteValue: byteValue,
                packetType: pType,
                value: getPacketValue(pType)
            });
        }
    }
    
    // =========================================================================
    // Turn Resolver Functions (mirrors turnResolver.ts)
    // =========================================================================
    
    /**
     * @notice Applies a packet effect to player
     * @dev Mirrors player-targeted logic in applyPacketEffect()
     */
    function applyPacketToPlayer(
        Player memory player,
        Packet memory packet
    ) internal pure returns (Player memory) {
        if (packet.packetType == PacketType.HEAL) {
            // Heal player, capped at maxHp
            uint16 newHp = player.hp + packet.value;
            player.hp = newHp > player.maxHp ? player.maxHp : newHp;
        } else if (packet.packetType == PacketType.DEFEND) {
            // Add shield
            player.shield += packet.value;
        }
        // ATTACK, CRIT, MISS on player = no effect in standard rules
        return player;
    }

    /**
     * @notice Applies a packet effect to enemy
     * @dev Mirrors enemy-targeted logic in applyPacketEffect()
     * @dev OVERKILL RULE: Excess damage is wasted
     * @dev IMMUNITY RULE: Cache only takes damage from CRIT.
     */
    function applyPacketToEnemy(
        Enemy memory enemy,
        Packet memory packet
    ) internal pure returns (Enemy memory) {
        // IMMUNITY RULE
        if (enemy.isCache) {
            if (packet.packetType == PacketType.CRIT) {
                // Instantly destroyed
                enemy.hp = 0;
            }
            // Else deflected (no damage)
            return enemy;
        }

        if (packet.packetType == PacketType.ATTACK || packet.packetType == PacketType.CRIT) {
            // Apply damage (overkill is wasted, HP cannot go below 0)
            if (enemy.hp > packet.value) {
                enemy.hp -= packet.value;
            } else {
                enemy.hp = 0;
            }
        }
        // MISS, DEFEND, HEAL on enemies = no effect
        return enemy;
    }

    /**
     * @notice Applies damage to player accounting for shield
     * @dev Mirrors applyDamageToPlayer() in turnResolver.ts
     */
    function applyDamageToPlayer(
        Player memory player,
        uint16 damage
    ) internal pure returns (Player memory) {
        uint16 remainingDamage = damage;
        
        // Shield absorbs first
        if (player.shield > 0) {
            if (player.shield >= remainingDamage) {
                player.shield -= remainingDamage;
                return player;
            } else {
                remainingDamage -= player.shield;
                player.shield = 0;
            }
        }
        
        // Remaining damage hits HP
        if (player.hp > remainingDamage) {
            player.hp -= remainingDamage;
        } else {
            player.hp = 0;
        }
        
        return player;
    }
    
    /**
     * @notice Executes enemy attack phase
     * @dev Mirrors executeEnemyPhase() in turnResolver.ts
     */
    function executeEnemyPhase(
        Player memory player,
        Enemy[] memory enemies
    ) internal pure returns (Player memory) {
        for (uint8 i = 0; i < enemies.length; i++) {
            // Only living enemies with ATTACK intent deal damage
            if (enemies[i].hp > 0 && enemies[i].isAttacking) {
                player = applyDamageToPlayer(player, enemies[i].damage);
            }
        }
        return player;
    }
    
    /**
     * @notice Checks win/loss conditions
     * @dev Mirrors checkTurnResult() in turnResolver.ts
     */
    function checkTurnResult(
        Player memory player,
        Enemy[] memory enemies
    ) internal pure returns (TurnResult) {
        // DEFEAT: Player HP <= 0
        if (player.hp == 0) {
            return TurnResult.DEFEAT;
        }
        
        // VICTORY: All enemies dead
        bool allEnemiesDead = true;
        for (uint8 i = 0; i < enemies.length; i++) {
            if (enemies[i].hp > 0) {
                allEnemiesDead = false;
                break;
            }
        }
        
        if (allEnemiesDead) {
            return TurnResult.VICTORY;
        }
        
        return TurnResult.CONTINUE;
    }

    /**
     * @notice Resolves a complete turn given state and assignments
     * @dev Main entry point - mirrors resolveTurn() in turnResolver.ts
     *      This is a PURE function that produces deterministic results.
     */
    function resolveTurn(
        bytes32 levelSeed,
        Player memory initialPlayer,
        Enemy[] memory initialEnemies,
        uint8 turn,
        Assignment[] memory assignments,
        uint32 initialScore
    ) internal pure returns (
        Player memory newPlayer,
        Enemy[] memory newEnemies,
        TurnResult result,
        uint32 newScore
    ) {
        // Generate hand from seed
        Packet[] memory hand = deriveHand(levelSeed, turn);
        
        // Validate assignment count
        require(assignments.length == hand.length, "Invalid assignment count");
        
        // Copy state for modification
        newPlayer = initialPlayer;
        newScore = initialScore;
        newEnemies = new Enemy[](initialEnemies.length);
        for (uint8 i = 0; i < initialEnemies.length; i++) {
            newEnemies[i] = initialEnemies[i];
        }
        
        // Phase 1: Apply player packet assignments
        for (uint8 i = 0; i < assignments.length; i++) {
            Packet memory packet = hand[assignments[i].packetIndex];
            uint8 targetType = assignments[i].targetType;
            
            if (targetType == 0) {
                // Trash - no effect
                continue;
            } else if (targetType == 1) {
                // Player target
                newPlayer = applyPacketToPlayer(newPlayer, packet);
            } else {
                // Enemy target (targetType - 2 = enemy index)
                uint8 enemyIndex = targetType - 2;
                require(enemyIndex < newEnemies.length, "Invalid enemy target");
                
                // Track HP before to see if it died this turn (simplified logic)
                // Actually easier to check dead caches after all packets applied
                newEnemies[enemyIndex] = applyPacketToEnemy(newEnemies[enemyIndex], packet);
            }
        }
        
        // Check for newly dead caches/enemies to award score
        // If it was alive in initial and dead in new, award points.
        for (uint8 i = 0; i < newEnemies.length; i++) {
            if (initialEnemies[i].hp > 0 && newEnemies[i].hp == 0) {
                // Standard Kill: +100 Points (Any unit)
                newScore += 100;

                // Jackpot: +1000 Points (Using CACHE_GOLD unit)
                if (newEnemies[i].isCache) {
                    newScore += 1000;
                }
            }
        }
        
        // Phase 2: Enemy attacks (only if game hasn't ended)
        result = checkTurnResult(newPlayer, newEnemies);
        if (result == TurnResult.CONTINUE) {
            newPlayer = executeEnemyPhase(newPlayer, newEnemies);
            // Re-check after enemy phase
            result = checkTurnResult(newPlayer, newEnemies);
        }
        
        // Bonus for Victory
        if (result == TurnResult.VICTORY) {
            // Completion Bonus: +500 Points
            newScore += 500;

            // Flawless Bonus: +200 Points (If Player HP == MaxHP)
            if (newPlayer.hp == newPlayer.maxHp) {
                newScore += 200;
            }
        }
    }
    
